// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract PharmaceuticalSupplyChain {
    enum Status { Created, Manufactured, Distributed, Dispensed }
    enum Role { Patient, Manufacturer, Distributor, Retailer }

    struct Drug {
        string drugName;
        string manufacturer;
        uint256 manufactureTimestamp;
        string batchNumber;
        string serialNumber;
        Status currentStatus;
        address manufacturerAddress;
        address[] supplyChainEntities;
        mapping(address => string) entityRoles;
        mapping(address => uint256) entityTimestamps;
    }

    // Drug tracking
    mapping(uint256 => Drug) public drugs;
    uint256 public drugCount;
    mapping(uint256 => string) public drugDocumentsIPFSHash;
    mapping(address => uint256[]) public manufacturerDrugs;  // Manufacturer address -> array of drug IDs

    // Access control
    address public owner;
    mapping(address => bool) public authorizedEntities;
    mapping(address => Role) public userRoles;

    // User management
    address[] public allUsers;
    mapping(address => bool) public isUserRegistered;
    mapping(Role => address[]) public usersByRole;

    // Events
    event DrugCreated(uint256 indexed id, string name, string manufacturer, string batchNumber);
    event StatusChanged(uint256 indexed id, Status newStatus, address updatedBy);
    event EntityAdded(uint256 indexed id, address entity, string role);
    event IPFSHashUpdated(uint256 indexed id, string ipfsHash);
    event RoleAssigned(address indexed user, Role role);
    event DrugAssigned(uint256 indexed drugId, address indexed manufacturer, string drugName);

    constructor() {
        owner = msg.sender;
        authorizedEntities[msg.sender] = true;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner can call this function");
        _;
    }

    modifier onlyAuthorized() {
        require(authorizedEntities[msg.sender], "Not authorized");
        _;
    }

    function _registerUser(address user, Role role) internal {
        if (!isUserRegistered[user]) {
            allUsers.push(user);
            isUserRegistered[user] = true;
        }
        usersByRole[role].push(user);
    }

    function authorizeEntity(address entity) public onlyOwner {
        authorizedEntities[entity] = true;
    }

    function isOwner(address account) public view returns (bool) {
        return account == owner;
    }

    function getUserRole(address user) public view returns (Role) {
        return userRoles[user];
    }

    function assignRole(address user, Role role) public onlyOwner {
        require(user != address(0), "Invalid address");

        // Handle role changes
        if (isUserRegistered[user]) {
            Role previousRole = userRoles[user];
            if (previousRole != role) {
                // Remove from previous role array
                address[] storage previousRoleUsers = usersByRole[previousRole];
                for (uint i = 0; i < previousRoleUsers.length; i++) {
                    if (previousRoleUsers[i] == user) {
                        previousRoleUsers[i] = previousRoleUsers[previousRoleUsers.length - 1];
                        previousRoleUsers.pop();
                        break;
                    }
                }
                usersByRole[role].push(user);
            }
        } else {
            _registerUser(user, role);
        }

        // Update role and authorization
        userRoles[user] = role;
        if (!authorizedEntities[user]) {
            authorizedEntities[user] = true;
        }

        emit RoleAssigned(user, role);
    }

    function getUsersByRole(Role role) public view returns (address[] memory) {
        return usersByRole[role];
    }

    // New function for admin to assign drugs to manufacturers
    function addDrugToManufacturer(
        address manufacturer,
        string memory drugName,
        string memory batchNumber,
        string memory serialNumber
    ) external onlyOwner returns (uint256) {
        require(userRoles[manufacturer] == Role.Manufacturer, "Address is not a manufacturer");
        require(bytes(drugName).length > 0, "Drug name required");

        uint256 newDrugId = drugCount++;

        Drug storage newDrug = drugs[newDrugId];
        newDrug.drugName = drugName;
        newDrug.manufacturer = addressToString(manufacturer); // Convert address to string
        newDrug.batchNumber = batchNumber;
        newDrug.serialNumber = serialNumber;
        newDrug.currentStatus = Status.Created;
        newDrug.manufacturerAddress = manufacturer;

        manufacturerDrugs[manufacturer].push(newDrugId);

        emit DrugCreated(newDrugId, drugName, addressToString(manufacturer), batchNumber);
        emit DrugAssigned(newDrugId, manufacturer, drugName);

        return newDrugId;
    }

    // Get all drugs assigned to a manufacturer
    function getManufacturerDrugs(address manufacturer) public view returns (uint256[] memory) {
        require(userRoles[manufacturer] == Role.Manufacturer, "Address is not a manufacturer");
        return manufacturerDrugs[manufacturer];
    }

    // Get count of manufacturer's drugs
    function getManufacturerDrugCount(address manufacturer) public view returns (uint256) {
        require(userRoles[manufacturer] == Role.Manufacturer, "Address is not a manufacturer");
        return manufacturerDrugs[manufacturer].length;
    }

    // Get drug IDs by index for a manufacturer
    function getManufacturerDrugAtIndex(address manufacturer, uint256 index) public view returns (uint256) {
        require(userRoles[manufacturer] == Role.Manufacturer, "Address is not a manufacturer");
        require(index < manufacturerDrugs[manufacturer].length, "Index out of bounds");
        return manufacturerDrugs[manufacturer][index];
    }

    // Helper function to convert address to string
    function addressToString(address _addr) internal pure returns (string memory) {
        bytes32 value = bytes32(uint256(uint160(_addr)));
        bytes memory alphabet = "0123456789abcdef";

        bytes memory str = new bytes(42);
        str[0] = '0';
        str[1] = 'x';
        for (uint256 i = 0; i < 20; i++) {
            str[2+i*2] = alphabet[uint8(value[i + 12] >> 4)];
            str[3+i*2] = alphabet[uint8(value[i + 12] & 0x0f)];
        }
        return string(str);
    }

    function createDrug(
        string memory _drugName,
        string memory _manufacturer,
        string memory _batchNumber,
        string memory _serialNumber
    ) public onlyAuthorized returns (uint256) {
        require(userRoles[msg.sender] == Role.Manufacturer, "Only manufacturers can create drugs");

        uint256 newDrugId = drugCount++;

        Drug storage newDrug = drugs[newDrugId];
        newDrug.drugName = _drugName;
        newDrug.manufacturer = _manufacturer;
        newDrug.manufactureTimestamp = block.timestamp;
        newDrug.batchNumber = _batchNumber;
        newDrug.serialNumber = _serialNumber;
        newDrug.currentStatus = Status.Created;
        newDrug.manufacturerAddress = msg.sender;

        newDrug.supplyChainEntities.push(msg.sender);
        newDrug.entityRoles[msg.sender] = "Manufacturer";
        newDrug.entityTimestamps[msg.sender] = block.timestamp;

        emit DrugCreated(newDrugId, _drugName, _manufacturer, _batchNumber);
        return newDrugId;
    }

    function updateStatus(uint256 _drugId, Status _newStatus) public onlyAuthorized {
        require(_drugId < drugCount, "Drug does not exist");
        require(uint(_newStatus) > uint(drugs[_drugId].currentStatus), "Cannot revert to previous status");

        if (_newStatus == Status.Manufactured) {
            require(userRoles[msg.sender] == Role.Manufacturer, "Only manufacturers can mark as manufactured");
        } else if (_newStatus == Status.Distributed) {
            require(userRoles[msg.sender] == Role.Distributor, "Only distributors can mark as distributed");
        } else if (_newStatus == Status.Dispensed) {
            require(userRoles[msg.sender] == Role.Retailer, "Only retailers can mark as dispensed");
        }

        drugs[_drugId].currentStatus = _newStatus;
        emit StatusChanged(_drugId, _newStatus, msg.sender);
    }

    function addEntityToSupplyChain(
        uint256 _drugId,
        address _entity,
        string memory _role
    ) public onlyAuthorized {
        require(_drugId < drugCount, "Drug does not exist");
        require(authorizedEntities[_entity], "Entity is not authorized");

        drugs[_drugId].supplyChainEntities.push(_entity);
        drugs[_drugId].entityRoles[_entity] = _role;
        drugs[_drugId].entityTimestamps[_entity] = block.timestamp;

        emit EntityAdded(_drugId, _entity, _role);
    }

    function setDrugDocumentsIPFSHash(uint256 _drugId, string memory _ipfsHash) public onlyAuthorized {
        require(_drugId < drugCount, "Drug does not exist");
        drugDocumentsIPFSHash[_drugId] = _ipfsHash;
        emit IPFSHashUpdated(_drugId, _ipfsHash);
    }

    function getDrugDocumentsIPFSHash(uint256 _drugId) public view returns (string memory) {
        require(_drugId < drugCount, "Drug does not exist");
        return drugDocumentsIPFSHash[_drugId];
    }

    function getDrugBasicInfo(uint256 _drugId) public view returns (
        string memory drugName,
        string memory manufacturer,
        uint256 manufactureTimestamp,
        string memory batchNumber,
        string memory serialNumber,
        Status currentStatus
    ) {
        require(_drugId < drugCount, "Drug does not exist");
        Drug storage drug = drugs[_drugId];

        return (
            drug.drugName,
            drug.manufacturer,
            drug.manufactureTimestamp,
            drug.batchNumber,
            drug.serialNumber,
            drug.currentStatus
        );
    }

    function getSupplyChainEntityCount(uint256 _drugId) public view returns (uint256) {
        require(_drugId < drugCount, "Drug does not exist");
        return drugs[_drugId].supplyChainEntities.length;
    }

    function getSupplyChainEntityAtIndex(uint256 _drugId, uint256 _index) public view returns (
        address entity,
        string memory role,
        uint256 timestamp
    ) {
        require(_drugId < drugCount, "Drug does not exist");
        require(_index < drugs[_drugId].supplyChainEntities.length, "Index out of bounds");

        address entityAddress = drugs[_drugId].supplyChainEntities[_index];
        return (
            entityAddress,
            drugs[_drugId].entityRoles[entityAddress],
            drugs[_drugId].entityTimestamps[entityAddress]
        );
    }
}
